// +build generate

package main

import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"log"
	"net/http"
)

func main() {
	resp, err := http.DefaultClient.Get("https://api.github.com/repos/sourcegraph/src-cli/releases")
	if err != nil {
		log.Fatal(err)
	}

	defer resp.Body.Close()
	respContent, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		log.Fatal(err)
	}

	payload := []struct {
		TagName string `json:"tag_name"`
	}{}
	if err := json.Unmarshal(respContent, &payload); err != nil {
		log.Fatal(err)
	}

	pre := `package httpapi

// Code generated by update_src_cli_version.go

// SrcCliVersion is the latest src-cli release at the time of this build
`

	fileContent := fmt.Sprintf("%svar SrcCliVersion = `%s`\n", pre, payload[0].TagName)

	if err := ioutil.WriteFile("src_cli_version.go", []byte(fileContent), 0666); err != nil {
		log.Fatal(err)
	}
}
